.meth_ing_wrap { width:100%; box-sizing:border-box;-moz-box-shadow: -1px 0px 15px #e0e0e0;-webkit-box-shadow: -1px 0px 15px #e0e0e0;box-shadow: -1px 0px 15px #e0e0e0; float:left; padding:15px; border-radius:4px; margin-bottom:20px;}
.meth_info_headline { margin:0 auto; font-size:18px; color:#17b3b6; background:#fff; font-weight:500; padding:0 0 10px; float:left;text-transform:uppercase; width: auto;}
.meth_ing_wrap span { font-size:18px; font-weight:600;padding:0; text-transform:capitalize; float:right;}
.meth_ing_info  { float:left;text-align:left; width:100%; box-sizing:border-box; margin:10px 0 0;}
.meth_ing_info ul { margin:0; padding:0;clear: both; text-align:center;}
.meth_ing_info ul li { list-style:none;  display:inline-block; padding:10px; background:#fafafa;border-radius:4px;width: 15%;margin: 0 5px 0 0;color:#17b3b6;text-transform: capitalize; }
.meth_ing_info ul li span { display:table;font-weight:500; color:#000;font-size:20px;float: inherit; margin: 0 auto;}
.meth_bot_info { width:100%; box-sizing:border-box; text-align:center; clear:both;display: inline-flex;}
.meth_bot_info span { font-size:14px; padding:10px 10px 0 10px; float:inherit; font-weight:400;display:table;margin: 0 auto;}
.meth_ing_info ul li span i { font-style:normal;font-size:12px; text-transform:lowercase;display:table-cell;}
.meth_bot_info span i { font-size:12px; font-style:normal; text-transform:lowercase;display:table-cell;}
.meth_ing_info ul li span.carbs,
.meth_ing_info ul li span.fat,
.meth_ing_info ul li span.cholestrol,
.meth_ing_info ul li span.protien,
.meth_ing_info ul li span.fiber { background-image:url(https://cms-test.stage.ndtv.com/static/images/nutrition_sprite.png); background-repeat:no-repeat; padding:0 0 0 30px;}
.meth_ing_info ul li span.fat { background-position:-3px -45px;}
.meth_ing_info ul li span.cholestrol { background-position:-3px -98px;}
.meth_ing_info ul li span.protien { background-position:-3px -152px;}
.meth_ing_info ul li span.fiber { background-position:0 -208px;}    

